import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import bookImage from '../images/bookImage.png';
import { FaDownload } from 'react-icons/fa';

const Books = () => {
    const [books, setBooks] = useState([]);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchAllBooks = async () => {
            try {
                const res = await axios.get("http://localhost:8800/books");
                setBooks(res.data);
            } catch (err) {
                console.log(err);
            }
        };
        fetchAllBooks();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete("http://localhost:8800/books/" + id);
            window.location.reload();
        } catch (err) {
            console.log(err);
        }
    }

    // Filter books based on search query
    const filteredBooks = books.filter(book => {
        return book.title.toLowerCase().includes(searchQuery.toLowerCase());
    });

    // Function to handle the download action
    const handleDownload = (bookId) => {
        // Add your download logic here, e.g., redirect to the download URL
        // For this example, let's assume the download URL is "/download/{bookId}"
        window.location.href = `/download/${bookId}`;
    }

    return (
        <div>
            {/* Add the search bar at the top */}
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Search for books"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />
            </div>
            <h1>BookSwapEDU</h1>
            <div className="books">
                {filteredBooks.map(book => (
                    <div className="book" key={book.id}>
                        <img src={bookImage} alt="Default" />
                        <h2>{book.title}</h2>
                        <p>{book.desc}</p>
                        <span>{book.price}</span>
                        <button className='delete' onClick={() => handleDelete(book.id)}>Delete</button>
                        <button className='update'>
                            <Link to={`/update/${book.id}`}>Update</Link>
                        </button>
                        <button className="formButton" onClick={() => handleDownload(book.id)}>
                            <FaDownload /> Download
                        </button>
                    </div>
                ))}
            </div>
            <button className='addnewbook'><Link to="/add">Add New Book</Link></button>
        </div>
    );
}

export default Books;
